                                    cmd.Parameters.AddWithValue("@inddate", inddate);
                                    cmd.Parameters.AddWithValue("@salesvalue", drr["salesvalue"].ToString());
                                    cmd.Parameters.AddWithValue("@paidamount", drr["paidamount"].ToString());
