                            }
                        }
                        GetRoutes.oppening = dr["Qty"].ToString();
                        IndentList.Add(GetRoutes);
                    }
                }
            }
            string response = GetJson(IndentList);
            context.Response.Write(response);
        }
        catch
        {
        }
