                        GetInventory.Qty = dr["Qty"].ToString();
                        GetInventory.ToadayQty = "";
                        InventoryList.Add(GetInventory);
                    }
                    string response = GetJson(InventoryList);
                    context.Response.Write(response);
                }
            }
        }
        catch
        {
        }
