                {
                    DCNO = "0000" + countdc;
                }
                if (countdc >= 10 && countdc <= 99)
                {
                    DCNO = "000" + countdc;
                }
                if (countdc >= 99 && countdc <= 999)
                {
                    DCNO = "00" + countdc;
                }
                if (countdc > 999 && countdc <= 9999)
                {
                    DCNO = "0" + countdc;
                }
                if (countdc > 9999)
